package Ch04_control_stmt;

public class Ch04_37_continue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//continue문
		for(int i=1; i<=10; i++) {
			if (i%2!=0) {//홀수인 경우
				continue;//아래의 출력문을 수행하지 않고 다음 반복으로 넘어가서 계속됨
			}
			
			System.out.println(i);
			
		}
		
	    	  }
	      
		}
		
		
		
		
